import "../registry/AbstractRNS.sol";

/**
 * @title PublicSubdomainRegistrar
 * Allows anyone to create subnodes under any delegated RNS node.
 */
contract PublicSubdomainRegistrar {
    AbstractRNS public rns;

    mapping (bytes32 => address) delegated;

    modifier onlyOwned (bytes32 node) {
        require(rns.owner(node) == address(this));
        _;
    }

    modifier onlyPreviousOwner (bytes32 node) {
        require(msg.sender == delegated[node]);
        _;
    }

    /**
     * @dev Constructor
     * @param _rns AbstractRNS RNS registry address
     */
    constructor (AbstractRNS _rns) public {
        rns = _rns;
    }

    /**
     * @dev Delegates an owned node to this contract.
     * @param node bytes32 The delegated RNS node.
     */
    function delegate (bytes32 node) public onlyOwned(node) {
        delegated[node] = msg.sender;
    }

    /**
     * @dev Check if a node is delegated to this contract.
     * @param node bytes32 An RNS node.
     * @return if the node is delgated
     */
    function isDelegated (bytes32 node) public view returns (bool) {
        return delegated[node] != address(0);
    }

    /**
     * @dev Transfers back a delegated node to who delegated it.
     * @param node bytes32 Node to retrive.
     */
    function transferBack (bytes32 node) public onlyPreviousOwner(node) {
        rns.setOwner(node, delegated[node]);
        delegated[node] = address(0);
    }

    /**
     * @dev Registrers a subnode under a given and delegated node.
     * @param node bytes32 The parent node.
     * @param label bytes32 The hash of the label specifying the subnode.
     */
    function register (bytes32 node, bytes32 label) public {
        address subnodeOwner = rns.owner(keccak256(abi.encodePacked(node, label)));
        require(subnodeOwner == address(0));

        rns.setSubnodeOwner(node, label, msg.sender);
    }
}

pragma solidity ^0.5.2;

import "../registry/AbstractRNS.sol";

/**
 * A simple resolver anyone can use; only allows the owner of a node to set its
 * address.
 */
contract PublicResolver {
    AbstractRNS rns;
    mapping(bytes32=>address) addresses;
    mapping(bytes32=>bytes32) hashes;

    modifier only_owner(bytes32 node) {
        require(rns.owner(node) == msg.sender);
        _;
    }

    /**
     * Constructor.
     * @param rnsAddr The RNS registrar contract.
     */
    constructor(AbstractRNS rnsAddr) public {
        rns = rnsAddr;
    }

    /**
     * Fallback function.
     */
    function() payable external {
        revert();
    }

    /**
     * Returns true if the specified node has the specified record type.
     * @param node The RNS node to query.
     * @param kind The record type name, as specified in EIP137.
     * @return True if this resolver has a record of the provided type on the
     *         provided node.
     */
    function has(bytes32 node, bytes32 kind) public view returns (bool) {
        return  (kind == "addr" && addresses[node] != address(0)) || 
        (kind == "hash" && hashes[node] != 0);
    }

    /**
     * Returns true if the resolver implements the interface specified by the provided hash.
     * @param interfaceID The ID of the interface to check for.
     * @return True if the contract implements the requested interface.
     */
    function supportsInterface(bytes4 interfaceID) public pure returns (bool) {
        return interfaceID == 0x3b3b57de || interfaceID == 0xd8389dc5;
    }

    /**
     * Returns the address associated with an RNS node.
     * @param node The RNS node to query.
     * @return The associated address.
     */
    function addr(bytes32 node) public view returns (address) {
        return addresses[node];
    }

    /**
     * Sets the address associated with an RNS node.
     * May only be called by the owner of that node in the RNS registry.
     * @param node The node to update.
     * @param addrValue The address to set.
     */
    function setAddr(bytes32 node, address addrValue) public only_owner(node) {
        addresses[node] = addrValue;
    }

    /**
     * Returns the content hash associated with an RNS node.
     * Note that this resource type is not standardized, and will likely change
     * in future to a resource type based on multihash.
     * @param node The RNS node to query.
     * @return The associated content hash.
     */
    function content(bytes32 node) public view returns (bytes32) {
        return hashes[node];
    }

    /**
     * Sets the content hash associated with an RNS node.
     * May only be called by the owner of that node in the RNS registry.
     * Note that this resource type is not standardized, and will likely change
     * in future to a resource type based on multihash.
     * @param node The node to update.
     * @param hash The content hash to set
     */
    function setContent(bytes32 node, bytes32 hash) public only_owner(node) {
        hashes[node] = hash;
    }
}

pragma solidity ^0.5.2;

import "../registry/AbstractRNS.sol";
import "../util/PaymentAdmin.sol";
import "../util/AbstractWhitelist.sol";
import "../token/ERC20Basic.sol";
import "../resolver/AbstractAddrResolver.sol";

/**
 * @title PriceSubdomainRegistrar
 * Allows anyone who is whitelisted to create subnodes under a given
 * RNS node and receive a price for doing this.
 */
contract PriceSubdomainRegistrar {
    address owner = msg.sender;

    PaymentAdmin public admin = new PaymentAdmin();
    AbstractRNS public rns;
    AbstractWhitelist public whitelist;
    ERC20Basic public token;
    bytes32 public rootNode;

    uint256 public price = 1 * (10 ** 18);

    bytes4 constant ADDR_SIGN = 0x3b3b57de;

    modifier onlyOwner () {
        require(msg.sender == owner);
        _;
    }

    modifier onlyWhitelisted () {
        require(whitelist.isWhitelisted(msg.sender));
        _;
    }

    /**
     * @dev Constructor
     * @param _rns AbstractRNS RNS registry address.
     * @param _whitelist AbstractWhitelist Whitelist to manage who can register domains.
     * @param _token ERC20Basic ERC-20 token address.
     * @param _rootNode bytes32 An owned node. The contract emits subnodes under this node.
     */
    constructor (AbstractRNS _rns, AbstractWhitelist _whitelist, ERC20Basic _token, bytes32 _rootNode) public {
        rns = _rns;
        whitelist = _whitelist;
        rootNode = _rootNode;
        token = _token;
    }

    /**
     * @dev Registrers a subnode under a given and delegated node. Who registers a domain
     * receives a token price.
     * @param label bytres32 The label of the new subnode.
     */
    function register (bytes32 label, address addr) public onlyWhitelisted() {
        bytes32 subnode = keccak256(abi.encodePacked(rootNode, label));
        require(rns.owner(subnode) == address(0));

        AbstractAddrResolver resolver = AbstractAddrResolver(rns.resolver(rootNode));
        require(resolver.supportsInterface(ADDR_SIGN));

        rns.setSubnodeOwner(rootNode, label, address(this));
        resolver.setAddr(subnode, addr);

        rns.setOwner(subnode, addr);
        whitelist.removeWhitelisted(msg.sender);
        admin.transfer(addr, token, price);
    }

    /**
     * @dev Sets the price given for regstring a subnode.
     * @param _price uint256 The new price.
     */
    function setPrice (uint256 _price) public onlyOwner() {
        price = _price;
    }

    /**
     * @dev Transfer the tokens stored in the token admin contract.
     * @param receiver address The address of the token receiver.
     * @param _token ERC20Basic The token to retrieve the founds of.
     */
    function retrieveTokens (address receiver, ERC20Basic _token) public onlyOwner() {
        admin.retrieveTokens(receiver, _token);
    }

  /**
   * @dev Transfers back the root node ownership to the contract's owner.
   */
  function transferBack () public onlyOwner() {
    rns.setOwner(rootNode, owner);
  }
}

